import { createSelector } from 'reselect'

const COLELCION_ID_MAP = {
    hats: 1,
    sneakers: 2,
    jackets: 3,
    womens: 4,
    mens: 5
}

const selectShop = state => state.shop;

export const selectShopCollections = createSelector(
    [selectShop],
    shop => shop.collections
)

export const selectShopCollectionsCategory = collectionUrlParam => createSelector(
    [selectShopCollections],
    shop => shop.collections.find(collection => collection.id == COLELCION_ID_MAP[collectionUrlParam])
)
