import { createSelector } from 'reselect'

const selectShop = state => state.shop;

export const selectShopCollections = createSelector(
    [selectShop],
    shop => shop.collections
)

export const selectShopColelctionsCategory = (title) => createSelector(
    [selectShop],
    shop => shop.collections.find(collection => collection.title == title)
)
