import { takeLatest, put } from 'redux-saga/effects';
import UserActionTypes from './user.types';
import { googleProvider, auth, createUserProfileDoc } from '../../firebase/firebase.utils';
import { googleSignInFailure, signInSucces, signInFailure } from './user.actions';
import { all, call } from 'redux-saga/effects';

export function* signInWithGoogle() {

    try {

        const { user } = yield auth.signInWithPopup(googleProvider);

        const userRef = yield call(createUserProfileDoc, user)

        const userSnapshot = yield userRef.get();

        yield put(signInSucces({
            id: userSnapshot.id,
            ...userSnapshot.data()
        }));

    } catch (err) {
        put(signInFailure(err))
    }

}

export function* onGoogleSignInStart() {
    yield takeLatest(UserActionTypes.GOOGLE_SIGN_IN_START, signInWithGoogle)
}

export function* signInWithEmailAndPassword({payload: {email, password}}) {

    try {

        const { user } = yield auth.signInWithEmailAndPassword(email, password);
        console.log(user)

        const userRef = yield call(createUserProfileDoc, user)

        const userSnapshot = yield userRef.get();

        yield put(signInSucces({
            id: userSnapshot.id,
            ...userSnapshot.data()
        }));

    } catch (err) {
        put(signInFailure(err))
    }
}

export function* onEmailAndPasswordSignInStart() {
    yield takeLatest(UserActionTypes.EMAIL_SIGN_IN_START, signInWithEmailAndPassword)
}


export function* userSagas() {
    yield all([
        call(onGoogleSignInStart),
        call(onEmailAndPasswordSignInStart)
    ])
}
