import React from "react";
import CollectionsOverview from "../../components/collections-overview/collections-overview.component";
import { Route } from "react-router-dom";
import CollectionPage from "../collection/collection.component";
import { createStructuredSelector } from "reselect";
import { connect } from "react-redux";
import { setCollections } from "../../redux/shop/shop.actions";
import { firestore } from "../../firebase/firebase.utils";

class ShopPage extends React.Component {
  constructor(props) {
    super(props);
  }

  async componentDidMount() {
    const { setCollections } = this.props;

    const colelctionsRef = firestore.collection("collections");
    const snapshot = await colelctionsRef.get();
    snapshot.onSnapshot(snap => {console.log(snap)})
  }

  render() {
    const { match } = this.props;

    return (
      <div className="shop-page">
        <Route exact path={`${match.path}`} component={CollectionsOverview} />
        <Route path={`${match.path}/:routeName`} component={CollectionPage} />
      </div>
    );
  }
}

const mapDispatchToProps = dispatch => ({
  setCollections: collections => dispatch(setCollections(collections))
});

export default connect(
  null,
  mapDispatchToProps
)(ShopPage);
